import React, { useEffect, useState} from 'react'
import { getAuthToken, request, setAuthHeader } from '../helpers/axios_helper';

function User() {
  const [userInfo, setUserInfo] = useState({
    "username": "",
    "email": "",
    "telefono": "",
    "direccion": "",
    "dni": "",
    "nombre": ""
  });
  useEffect(() => {
    request("GET", "/users/g").then(
      (response) => {
        console.log(response.data);
        setUserInfo({
          "username": response.data.username,
          "email": response.data.email,
          "telefono": response.data.telefono,
          "direccion": response.data.direccion,
          "dni": response.data.dni,
          "nombre": response.data.nombre
        });
      }
    ).catch(
      (error) => {
        console.log(error);
      }
    );
  }, []);

  return (
    <div>
      <h1>Mi cuenta</h1>
      <p>En esta sección podrás ver tu información personal y modificarla si es necesario.</p>
      <h2>Información personal</h2>
      <p>Nombre: {userInfo.nombre}</p>
      <p>DNI: {userInfo.dni}</p>
      <p>Dirección: {userInfo.direccion}</p>
      <p>Email: {userInfo.email}</p>
      <p>Teléfono: {userInfo.telefono}</p>


    </div>
  )
}

export default User
