// Home.js
import React, { useEffect, useState } from 'react';
import { request } from '../helpers/axios_helper';

const Home = () => {

  const [tools, setTools] = useState([]);

  useEffect (()=> {
    request('GET', '/herramientas')
    .then((response) => {
      console.log(response.data);

    })
    .catch((error) => {
      console.log('Error:', error);
    });

  }, []);

  return (
    <div className='home'>
      <h2>Welcome to DIY4Rent</h2>
      <p>Find the tools you need for your projects!</p>
      {tools.length > 0 ? (
      <ul>
        {tools.map(tool => (
          <li key={tool.id}>
            <h3>{tool.tipo}</h3>
            <p>Descripcion: {tool.descripcion}</p>
            <p>Precio diario: ${tool.precioDiario}</p>
            <p>Disponibilidad: {tool.disponibilidad ? 'Available' : 'Not Available'}</p>
            <p>Estado FÃ­sico: {tool.estadoFisico}</p>
            <p>Propietario: {tool.usuario.nombre}</p>
          </li>
        ))}
      </ul>) : (
        <p>Loading tools...</p>
      )  
      }
    </div>
  );
};

export default Home;
