// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import SearchTools from './pages/SearchTools';
import Home from './pages/Home';
import About from './pages/About';
import Contact from './pages/Contact';
import Login from './pages/Login';
import './App.css';
import { request, setAuthHeader } from '../helpers/axios_helper';

function App() {



  const [componentToShow, setComponentToShow] = useState('welcome');

  const login = () => setComponentToShow('login');
  const logout = () => {
    setAuthHeader(null);
    setComponentToShow('welcome');
  };

  const onLogin = async (e, username, password) => {
    e.preventDefault();
    try {
      const response = await request('POST', '/login', {
        login: username,
        password,
      });
      setAuthHeader(response.data.token);
      setComponentToShow('messages');
    } catch (error) {
      setAuthHeader(null);
      setComponentToShow('welcome');
    }
  };

  const onRegister = async (event, firstName, lastName, username, password) => {
    event.preventDefault();
    try {
      const response = await request('POST', '/register', {
        firstName,
        lastName,
        login: username,
        password,
      });
      setAuthHeader(response.data.token);
      setComponentToShow('messages');
    } catch (error) {
      setAuthHeader(null);
      setComponentToShow('welcome');
    }
  };


  return (
    <Router>
      <div className="app">
        <nav className="navbar">
          <div className="container">
            <h1 className="logo">DIY4Rent</h1>
            <ul className="menu">
              <li className="menu-item">
                <Link to="/">Home</Link>
              </li>
              <li className="menu-item">
                <Link to="/search">Search Tools</Link>
              </li>
              <li className="menu-item">
                <Link to="/about">About</Link>
              </li>
              <li className="menu-item">
                <Link to="/contact">Contact</Link>
              </li>
              <li className="menu-item">
                <Link to="/login">Iniciar sesión</Link>
              </li>
              <li className="menu-item">
                <Link to="/login">Registrarse</Link>
              </li>
            </ul>
          </div>
        </nav>
        <div className="content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/search" element={<SearchTools />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
